import React, { useState } from "react";
import { ReactComponent as MenuIcon } from "../../assets/icons/mobile-menu-icon.svg";
//import { capitalizeFirstLetter } from '../../utils/helpers';

function Nav(props) {
  const [mobileNavOpen, setMobileNavOpen] = useState(false);
  const tabs = ["Homepage", "About", "Projects", "Contact"];
  function onClick() {
    setMobileNavOpen(true);
  }
  return (
    <header className="Header-Container">
      <h1 className="Heading">Sam</h1>
      <nav className="container-fluid">
        {/* <div className="Mobile-Nav">
          <button className="Mobile-Nav-Btn" onClick={onClick}>
            <MenuIcon />
          </button>
        </div> */}
        <div>
          <ul className="navs nav-tab row">
            {tabs.map((tab) => (
              <li className="nav-item col mx-5" key={tab}>
                <a
                  href={"#" + tab.toLowerCase()}
                  // Whenever a tab is clicked on,
                  // the current page is set through the handlePageChange props.
                  onClick={() => props.handlePageChange(tab)}
                  className={
                    props.currentPage === tab ? "nav-link active" : "nav-link"
                  }
                >
                  {tab}
                </a>
              </li>
            ))}
          </ul>
        </div>
      </nav>
    </header>
  );
}

export default Nav;
