import React, { useEffect, useState } from "react";

function RollingNumber({ initialValue, finalValue, duration }) {
  const [currentValue, setCurrentValue] = useState(initialValue);

  useEffect(() => {
    let startTime;
    const animate = (time) => {
      if (!startTime) startTime = time;
      const progress = (time - startTime) / duration;
      if (progress > 1) {
        setCurrentValue(
          Math.floor(initialValue + (finalValue - initialValue) * progress)
        );
        requestAnimationFrame(animate);
      } else {
        setCurrentValue(finalValue);
      }
    };
    requestAnimationFrame(animate);

    return () => {
      setCurrentValue(finalValue);
    };
  }, [initialValue, finalValue, duration]);

  return <span>{currentValue}</span>;
}

function Homepage() {
  return (
    <div className="container homepage mx-5">
      <div className="maybe row">
        <div className="circle col-5 "></div>
        <div className="col-7 mx-auto hwords">
          <h1 className="home">Hello!</h1>
          <p className="homep">My name is Samantha and I am a web developer.</p>
        </div>
      </div>
    </div>
  );
}

export default Homepage;
